

function levels(arr,level,ans,sid){
   ans[sid]=level;
   const reporteesArr = arr.find((item) => item.id == sid).reportees;

   reporteesArr.forEach((item)=>{
    levels(arr,level+1,ans,item);
   })

}


function getStarting(arr){

    let ans={};

    let level0s=[];
    let obj={};
    arr.forEach((item)=>{
      const {reportees}=item;
reportees.forEach((rid)=>{
    obj[rid] = true;
});

    })

     arr.forEach((item) => {
       const { id } = item;
       if (!obj[id]) level0s.push(id);
     });

    console.log(level0s);

    level0s.forEach((item)=> levels(arr,0,ans,item))
return ans;
   
    
}


